                        var tmpEnd = domUtils.getXY(tmpCell)[flag ? 'x' : 'y'] + tmpCell["offset" + (flag ? 'Width' : 'Height')];
                        //絲劫�茵���td綏牙�頮���∵�rowSpan篋�
                        if (tmpEnd > end && flag) break;
