<template>
    <div class='person'>
      <img  :src='src' class='head' width="50%" height="100%">
      <div class='info' >{{name}}</div>
      <div>
        <img class='arrow' @click=infoDisplay :src='arrow' width='10%' height='10%' />
      </div>
    </div>
</template>
<style scoped>
    .person{
      float : right;
      height: 100%;
      width:10%;
    }
    .head{
      float:left;
      border-radius : 50%;
    }
    .info{
      float:left;
      width:50%;
      height:40%;
      margin-top:10%;
    }

</style>
<script>
    import store from '../../store'
    import {getUserInfo, handlePicPath} from '../../tools'
    export default{
        data(){
              let userInfo = getUserInfo();
              return{
                src : handlePicPath(userInfo.headPicture),
                name : userInfo.userName,
                arrowDown : 'data:image/png;base64,',
                arrowUp : 'data:image/png;base64,',
                arrow : 'data:image/png;base64,'

              }

        },
        methods :{
           infoDisplay(){
                store.dispatch('changeDisplay');
                this.arrow = this.arrow == this.arrowDown ? this.arrowUp : this.arrowDown;
           }
        },
        components:{

        }
    }
</script>
